
/*
  Note: We have included the plugin in the same JavaScript file as the TinyMCE
  instance for display purposes only. Tiny recommends not maintaining the plugin
  with the TinyMCE instance and using the `external_plugins` option.
*/
tinymce.PluginManager.add('bacajuga', function(editor, url) {
  var openDialog = function () {
    return editor.windowManager.open({
      title: 'Tambahkan Baca Juga',
      body: {
        type: 'panel',
        items: [
          {
            type: 'input',
            name: 'title',
            label: 'URL'
          }
        ]
      },
      buttons: [
        {
          type: 'cancel',
          text: 'Batal'
        },
        {
          type: 'submit',
          text: 'Simpan',
          primary: true
        }
      ],
      onSubmit: function (api) {
        var data = api.getData();
        /* Insert content when the window form is submitted */
        editor.insertContent(`<div id="snippet-link" data-url="${data.title}">
            <div class="small text-muted pl-3 my-2 border-left">
              <b class="text-danger mr-2">URL:</b>${data.title}
            </div>
          </div>`);
        api.close();
      }
    });
  };
  /* Add a button that opens a window */
  editor.ui.registry.addButton('bacajuga', {
    text: 'Bacajuga',
    onAction: function () {
      /* Open window */
      openDialog();
    }
  });
  /* Adds a menu item, which can then be included in any menu via the menu/menubar configuration */
  editor.ui.registry.addMenuItem('bacajuga', {
    text: 'Example plugin',
    onAction: function() {
      /* Open window */
      openDialog();
    }
  });
  /* Return the metadata for the help plugin */
  return {
    getMetadata: function () {
      return  {
        name: 'Example plugin',
        url: 'http://exampleplugindocsurl.com'
      };
    }
  };
});



